import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../_services/auth.service';
import { Router } from '@angular/router';
import { HttpErrorResponse } from '@angular/common/http';
import { User } from '../_models/user';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.css']
})
export class WelcomeComponent {

LgForm!: FormGroup;
submitted = false;
loading = false;
isLoginError : boolean = false;
UserName: any;
Password: any;
user= new User();

constructor( private fg : FormBuilder, private auth : AuthService, private router: Router, private toastr: ToastrService){}

ngOnInit(): void{
  this.LgForm = this.fg.group({

    UserName: ['', Validators.required],
    Password: ['', Validators.required],

  });

}

get f() { return this.LgForm.controls; }

  onSubmit() {
    this.submitted = true;
    if (this.LgForm.invalid) {
      return;
    }else{

    this.loading = true;
    // this.toastr.success('Successful');


    this.auth.login(this.LgForm.value).subscribe((token : string) => {
      localStorage.setItem('authToken', token);
      this.toastr.success('Successful');
      console.log(this.LgForm.value);
      this.router.navigate(['/list']);

    },
      err=>{
        alert("Wrong UserName or Password");
      });

    }

  }









}
<head>
	<meta charset="UTF-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<meta name="author" content="Kagiso">
	<title>Login Page</title>
</head>
<body>
	<!-- Main Content -->
	<div class="container-fluid">
		<div class="row main-content bg-success text-center">
			<div class="col-md-4 text-center company__info">
				<span class="company__logo"><h2><span class="fa fa-android"></span></h2></span>
				<h4 class="company_title">Company Logo</h4>
			</div>
			<div class="col-md-8 col-xs-12 col-sm-12 login_form ">
				<div class="container-fluid">
					<div class="row">
						<h2>Log In</h2>
					</div>
					<div class="row">
						<form [formGroup]="LgForm" (ngSubmit)="onSubmit()" class="form-group">
							<div class="row">
                <input
                class="form__input"
                type="text"
                formControlName="UserName"
                placeholder="UserName"
                [ngClass]="{ 'is-invalid': submitted && f['UserName'].errors }"
               />
              <div *ngIf="submitted && f['UserName'].errors" class="invalid-feedback">
                <div *ngIf="f['UserName'].errors?.['required']">Username is required</div>
              </div>
							</div>
							<div class="row">
								<!-- <span class="fa fa-lock"></span> -->
                <input
                class="form__input"
                type="password"
                formControlName="Password"
                placeholder="Password"
                [ngClass]="{ 'is-invalid': submitted && f['Password'].errors }"
                 />
              <div *ngIf="submitted && f['Password'].errors" class="invalid-feedback">
                <div *ngIf="f['Password'].errors?.['required']">Password is required</div>
              </div>
							</div>
							<!-- <div class="row">
								<input type="checkbox" name="remember_me" id="remember_me" class="">
								<label for="remember_me">Remember Me!</label>
							</div> -->
							<div class="row">
								<input type="submit" value="Submit" class="btn">
							</div>
						</form>
					</div>
					<div class="row">
						<p>Don't have an account? <a routerLink="/reguser">Register Here</a></p>
					</div>
				</div>
			</div>
		</div>
	</div>
	<!-- Footer -->
	<div class="container-fluid text-center footer">
		Coded with &hearts; by <a href="https://linkedin/kagiso-shibambo">Kagiso.</a>
	</div>
</body>

