import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../_services/auth.service';
import { Router } from '@angular/router';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.css']
})
export class WelcomeComponent {

loginForm!: FormGroup;
submitted = false;
isLoginError : boolean = false;
UserName : any;
Password : any;

constructor( private fg : FormBuilder, private auth : AuthService, private router: Router){}

ngOnInit(): void{
  this.loginForm = this.fg.group({
   UserName : ['', Validators.required],
   Password : ['', Validators.required],
  })
}

get f() { return this.loginForm.controls; }

  onLogin(UserName : any, Password : any) {
    this.auth.login(UserName,Password).subscribe((data : any)=>{
      localStorage.setItem('userToken', data.access_token);
      this.router.navigate(['/employee']);
    },
      (err : HttpErrorResponse)=>{
           this.isLoginError = true;
      });

    // if(this.loginForm.valid){
    //   console.log(this.loginForm.value)

    //   this.auth.login(this.loginForm.value)
    //   .subscribe({
    //     next:(res) =>{
    //       alert(res.message);
    //       this.loginForm.reset();
    //       this.router.navigate(['list'])
    //     },
    //     error:(err) => {
    //       alert(err?.error.message)
    //     }
    //   })

    // }

  }


}
<form  (ngSubmit)="onLogin(UserName.value, Password.value)">

    <div class="row">
        <div class="form-group mb-3">
            <label class="mb-2">UserName</label>
            <input type="UserName"   #UserName ngModel name="UserName" class="form-control"
            [ngClass]="{ 'is-invalid': submitted && f['UserName'].errors }" />

            <div *ngIf="submitted && f['UserName'].errors" class="invalid-feedback">
                <div *ngIf="f['UserName'].errors">UserName is required</div>
            </div>

        </div>
    </div>

    <div class="row">
        <div class="form-group mb-3">
            <label class="mb-2">Password</label>
            <input type="Password"  #Password ngModel name="Password" class="form-control"
            [ngClass]="{ 'is-invalid': submitted && f['Password'].errors }" />

            <div *ngIf="submitted && f['Password'].errors" class="invalid-feedback">
                <div *ngIf="f['Password'].errors">Password is required</div>
            </div>

        </div>
    </div>




    <button type="submit" class="btn btn-success me-3">Submit</button><br>
    <a routerLink="/registration">Register Here</a>
</form>
