import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../_services/auth.service';
import { Router } from '@angular/router';
import { HttpErrorResponse } from '@angular/common/http';
import { User } from '../_models/user';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.css']
})
export class WelcomeComponent {

loginForm!: FormGroup;
submitted = false;
isLoginError : boolean = false;
UserName: any;
Password: any;
user= new User();

constructor( private fg : FormBuilder, private auth : AuthService, private router: Router){}

ngOnInit(): void{

}

get f() { return this.loginForm.controls; }

  onLogin(user: User) {
    this.auth.login(user).subscribe((token : string) => {
      localStorage.setItem('authToken', token);
      alert("Successfully Logged In");
      this.router.navigate(['/list']);
    },
      (err : HttpErrorResponse)=>{
           this.isLoginError = true;
      });



  }


}
<!-- Section: Design Block -->
<section class=" text-center text-lg-start">
    <style>

      .rounded-t-5 {
        border-top-left-radius: 0.5rem;
        border-top-right-radius: 0.5rem;
      }

      @media (min-width: 992px) {
        .rounded-tr-lg-0 {
          border-top-right-radius: 0;
        }

        .rounded-bl-lg-5 {
          border-bottom-left-radius: 0.5rem;
        }
      }
    </style>
    <div class="card mb-3">
      <div class="row g-0 d-flex align-items-center">
        <div class="col-lg-4 d-none d-lg-flex">
          <img src="https://mdbootstrap.com/img/new/ecommerce/vertical/004.jpg" alt="Trendy Pants and Shoes"
            class="w-100 rounded-t-5 rounded-tr-lg-0 rounded-bl-lg-5" />
        </div>
        <div class="col-lg-8">
          <div class="card-body py-5 px-md-5">

            <form>
              <!-- Email input -->
              <div class="form-outline mb-4">
                <input type="username" [(ngModel)]= "user.UserName" name="UserName" class="form-control" />
                <label class="form-label" for="form2Example1">Username</label>
              </div>

              <!-- Password input -->
              <div class="form-outline mb-4">
                <input type="password" [(ngModel)]= "user.Password" name="Password" class="form-control" />
                <label class="form-label" for="form2Example2">Password</label>
              </div>



              <!-- Submit button -->
              <button type="button" (click)="onLogin(user)"
               class="btn btn-primary btn-block mb-4">Sign in</button><br>
               <a routerLink="/reguser">Register Here</a>

            </form>

          </div>
        </div>
      </div>
    </div>
  </section>
  <!-- Section: Design Block -->


