              <div class="col-md-6 col-lg-7 d-flex align-items-center">
                <div class="card-body p-4 p-lg-5 text-black">

                  <form [formGroup]="LgForm">

                    <div class="d-flex align-items-center mb-3 pb-1">
                      <i class="fas fa-cubes fa-2x me-3" style="color: #ff6219;"></i>
                      <span class="h1 fw-bold mb-0">Logo</span>
                    </div>

                    <h5 class="fw-normal mb-3 pb-3" style="letter-spacing: 1px;">Sign into your account</h5>

                    <div class="form-outline mb-4">
                      <input type="username"  [(ngModel)]= "user.UserName" name="UserName" class="form-control form-control-lg" />
                      <label class="form-label" for="form2Example17">Username</label>

                    </div>

                    <div class="form-outline mb-4">
                      <input type="password" [(ngModel)]= "user.Password" name="Password" class="form-control form-control-lg" />
                      <label class="form-label" for="form2Example27">Password</label>
                    </div>

                    <div class="pt-1 mb-4">
                      <button type="button" (click)="onLogin(user)" class="btn btn-dark btn-lg btn-block" >Login</button>
                    </div>


                    <p class="mb-5 pb-lg-2" style="color: #393f81;">Don't have an account? <a routerLink="/reguser"
                        style="color: #393f81;">Register here</a></p>
                    <a href="#!" class="small text-muted">Terms of use.</a>
                    <a href="#!" class="small text-muted">Privacy policy</a>
                  </form>

                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>

