import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { EmpService } from '../_services/emp.service';

@Component({
  selector: 'app-addedit',
  templateUrl: './addedit.component.html',
  styleUrls: ['./addedit.component.css']
})
export class AddeditComponent {

  formadd!: FormGroup;
  id!: number;
  loading = false;
  submitted = false;
  btnText: string = "Save";
  title: string = "New Employee";

  constructor(
    private formBuilder: FormBuilder,
    private empService: EmpService,
    private router: Router,
    private route: ActivatedRoute
  ) { }

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];

    if (this.id > 0) {
      this.loadData();
      this.btnText = "Update";
      this.title = "Edit Employee";
    }

    this.formadd = this.formBuilder.group({
      empId: 0,
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      email: ['', Validators.required],
      phoneNumber: ['', Validators.required]
    });
  }

  // convenience getter for easy access to form fields
  get f() { return this.formadd.controls; }

  loadData() {
    this.empService.getById(this.id).subscribe(x => this.formadd.patchValue(x))
  }

  onSubmit() {

    this.submitted = true;

    // stop here if form is invalid
    if (this.formadd.invalid) {
      return;
    }

    this.loading = true;

    if (this.id > 0) {
      this.empService.update(this.formadd.value)
        .subscribe(
          data => {
            this.btnCancel();
          })
    }
    else {
      this.empService.create(this.formadd.value)
        .subscribe(
          data => {
            this.btnCancel();
          });
    }
  }

  btnCancel() {
    this.router.navigate([''])
  }

}
